DROP TABLE IF EXISTS `user_collect_info`;
CREATE TABLE `user_collect_info`  (
  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '主键',
  `user_id` bigint(20) UNSIGNED NULL DEFAULT NULL,
  `type` int(1) UNSIGNED NULL DEFAULT NULL COMMENT '1 收藏文章 2 点赞文章 3收藏评论 4点赞评论',
  `c_id` bigint(20) UNSIGNED NULL DEFAULT NULL,
  `create_time` datetime NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  INDEX `user_id_2`(`user_id`, `type`, `c_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 6152 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;

